import PropTypes from "prop-types";
import React, { Component } from "react";
import {Container, Header } from "semantic-ui-react";
import moment from "moment";
import momentDurationFormatSetup from "moment-duration-format";

momentDurationFormatSetup(moment);

export default class HomepageHeading extends Component {
  constructor(props) {
    super(props);
    this.state = {

    };
  }

  render() {
    const { mobile } = this.props;

    return (
      <Container text textAlign="center">

        <div>
          <Header
            as="h1"
            content="Hi, I'm Yacob Madiana"
            inverted
            style={{
              fontSize: mobile ? "1.5em" : "2em",
              fontWeight: "normal",
              marginBottom: 0,
              marginTop: mobile ? "1.5em" : "3em"
            }}
          />
          <Header
            as="h2"
            content="Application Developer in a wide variety of business & social applications. Particularly interested in application development using modern javascript technologies (React, node JS). Always interested in mobile and web application development projects. I also able to deliver serverless solution to your cloud infrastructure using AWS products to reduce production costs in Software Development Projects."
            inverted
            style={{
              fontSize: mobile ? "1.5em" : "1.7em",
              fontWeight: "normal",
              marginTop: mobile ? "0.5em" : "1.5em"
            }}
          />
        </div>
      </Container>
    );
  }
}

HomepageHeading.propTypes = {
  mobile: PropTypes.bool
};
