import PropTypes from "prop-types";
import React, { Component } from "react";
import { Container, Header } from "semantic-ui-react";
import moment from "moment";
import momentDurationFormatSetup from "moment-duration-format";

momentDurationFormatSetup(moment);

export default class HomepageHeading extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    const { mobile } = this.props;

    return (
      <Container text textAlign="left">
        <div>
          <Header
            as="h1"
            content="Hi, I'm Yacob Madiana"
            inverted
            style={{
              fontSize: mobile ? "1.5em" : "2em",
              fontWeight: "normal",
              marginBottom: 0,
              marginTop: mobile ? "1.5em" : "3em"
            }}
          />
          <Header
            as="h2"
            content="Full Stack Web Developer"
            inverted
            style={{
              fontSize: mobile ? "1.5em" : "1.7em",
              fontWeight: "normal",
              marginTop: mobile ? "0.5em" : "1.5em"
            }}
          />
          <Container text>
            <p>
              Application Developer in a wide variety of business & social
              applications. Particularly interested in application development
              using modern javascript technologies (React, node JS). Always
              interested in mobile and web application development projects. I
              also able to deliver serverless solution to your cloud
              infrastructure using AWS products to reduce production costs in
              Software Development Projects.
            </p>
            <p>
              <p>I am comfortable with:</p>
              <p>Programming Languages: Javascript, Java</p>
              <p>Web Technologies: Node JS, React JS, REST API</p>
              <p>Databases: mongoDB, DynamoDB.</p>
              <p>Operating System: MacOS, Debian/Arch based Linux Distros.</p>
              <p>Revision Control: Git</p>
              <p>CI/CD: Travis, Gitlab</p>
              <p>
                Familiar with following AWS Products: EC2, Elastic Beanstalk,
                Lambda, API Gateway, DynamoDB, S3.
              </p>
              <p>My Github page: https://github.com/yacob89 </p>
            </p>
          </Container>
        </div>
      </Container>
    );
  }
}

HomepageHeading.propTypes = {
  mobile: PropTypes.bool
};
